INSERT INTO organizations (id, name, tag, phone_number, address, description, created_at, updated_at) VALUES (1, 'Apple', 'APPLE', '0900000001', 'Cupertino, California, Mỹ', 'Apple Inc. là một tập đoàn công nghệ máy tính của Mỹ có trụ sở chính đặt tại Cupertino, California. Apple được thành lập ngày 1 tháng 4 năm 1976 dưới tên Apple Computer, Inc., và đổi tên thành Apple Inc. vào đầu năm 2007.', '2019-09-05', '2019-09-05');
INSERT INTO organizations (id, name, tag, phone_number, address, description, created_at, updated_at) VALUES (2, 'Microsoft', 'MS', '0900000002', 'Redmond, Washington, Hoa Kỳ', 'Microsoft là một tập đoàn đa quốc gia của Hoa Kỳ đặt trụ sở chính tại Redmond, Washington; chuyên phát triển, sản xuất, kinh doanh bản quyền phần mềm và hỗ trợ trên diện rộng các sản phẩm và dịch vụ liên quan đến máy tính.', '2019-09-05', '2019-09-05');
INSERT INTO organizations (id, name, tag, phone_number, address, description, created_at, updated_at) VALUES (3, 'Amazon', 'AMZ', '0900000003', 'Seattle, Washington, Hoa Kỳ', 'Amazon Inc là một công ty công nghệ đa quốc gia của Mỹ có trụ sở tại Seattle, Washington tập trung vào thương mại điện tử, điện toán đám mây, phát trực tuyến kỹ thuật số, và trí tuệ nhân tạo. Công ty này được coi là một trong những công ty công nghệ Big Four cùng với Google, Apple và Facebook.', '2019-09-05', '2019-09-05');
INSERT INTO organizations (id, name, tag, phone_number, address, description, created_at, updated_at) VALUES (4, 'Google', 'GOOGLE', '0900000004', 'Mountain View, California, Hoa Kỳ', 'Google LLC là một công ty công nghệ đa quốc gia của Mỹ, chuyên về các dịch vụ và sản phẩm liên quan đến Internet, bao gồm các công nghệ quảng cáo trực tuyến, công cụ tìm kiếm, điện toán đám mây, phần mềm và phần cứng. Đây được coi là một trong những công ty công nghệ Big Four, cùng với Amazon, Apple và Facebook.', '2019-09-05', '2019-09-05');
INSERT INTO organizations (id, name, tag, phone_number, address, description, created_at, updated_at) VALUES (5, 'Berkshire Hathaway', 'BERK', '0900000005', 'Kiewit Plaza, Omaha, Nebraska, Hoa Kỳ', 'Berkshire Hathaway Inc. là một công ty holding conglomerate đa quốc gia có trụ sở tại Omaha, Nebraska, Hoa Kỳ. Công ty sở hữu toàn bộ các công ty GEICO, Dairy Queen, BNSF Railway, Lubrizol, Fruit of the Loom, Helzberg Diamonds, Long & Foster, FlightSafety International, Pampered Chef, và NetJets...', '2019-09-05', '2019-09-05');
INSERT INTO organizations (id, name, tag, phone_number, address, description, created_at, updated_at) VALUES (6, 'Facebook', 'FACE', '0900000006', 'Palo Alto, California Dublin, Ireland', 'Facebook, Inc là một công ty phương tiện truyền thông xã hội và dịch vụ mạng xã hội trực tuyến thành lập vào năm 2004 của Mỹ có trụ sở tại Menlo Park, California. Nó được Mark Zuckerberg, cùng với các sinh viên Đại học Harvard và bạn cùng phòng là Eduardo Saverin, Andrew McCollum, Dustin Moskovitz, Chris Hughes sáng lập. Đây được coi là một trong những công ty công nghệ Big Four cùng với Amazon, Apple và Google.', '2019-09-05', '2019-09-05');
INSERT INTO organizations (id, name, tag, phone_number, address, description, created_at, updated_at) VALUES (7, 'Alibaba ', 'ALBB', '0900000007', 'Hàng Châu, Trung Quốc', 'Alibaba (chữ Hán:  阿里巴巴集团控股有限公司, Công ty Hữu hạn Cổ phần Tập đoàn Alibaba) là một tập đoàn thương mại điện tử cung cấp dịch vụ bán hàng giữa người tiêu dùng với người tiêu dùng,doanh nghiệp tới người tiêu dùng và doanh nghiệp với doanh nghiệp dịch vụ bán hàng thông qua cổng thông tin điện tử.', '2019-09-05', '2019-09-05');
INSERT INTO organizations (id, name, tag, phone_number, address, description, created_at, updated_at) VALUES (8, 'Tencent', 'TENCENT', '0900000008', 'Thâm Quyến, Quảng Đông, Cộng hòa Nhân dân Trung Hoa', 'Tencent Holdings Limited (tiếng Trung: "腾讯控股有限公司"); là một công ty cổ phần đầu tư Trung Quốc với các công ty con cung cấp dịch vụ truyền thông, giải trí, Internet và dịch vụ giá trị gia tăng điện thoại di động, và hoạt động các dịch vụ quảng cáo trực tuyến tại Trung Quốc. Tập đoàn này sở hữu mạng xã hội WeChat với hơn 1 tỷ người sử dụng.', '2019-09-05', '2019-09-05');
INSERT INTO organizations (id, name, tag, phone_number, address, description, created_at, updated_at) VALUES (9, 'JPMorgan Chase', 'JPMOR', '0900000009', 'Thành phố New York, New York, Mỹ', 'JPMorgan Chase (NYSE: JPM) là một trong những hãng dịch vụ tài chính lâu đời nhất trên thế giới. Công ty này có trụ sở tại Thành phố New York, là đơn vị hàng đầu trong dịch vụ tài chính, lĩnh vực ngân hàng đầu tư và quản lý tài sản. Tài sản của tập đoàn này hiện là 2.441 tỷ USD, là ngân hàng lớn nhất ở Hoa Kỳ,[1]. Quỹ tự bảo hiểm rủi ro của JPMorgan Chase là quỹ lớn nhất Hoa Kỳ với tài sản 34 tỷ USD năm 2007.', '2019-09-05', '2019-09-05');
INSERT INTO organizations (id, name, tag, phone_number, address, description, created_at, updated_at) VALUES (10, 'Johnson & Johnson', 'JOHNSON', '0900000010', 'One Johnson & Johnson Plaza, New Brunswick, New Jersey, Hoa Kỳ', 'Johnson & Johnson là một công ty dược phẩm, thiết bị y tế và đóng gói hàng hóa tiêu dùng của Hoa Kỳ được thành lập vào năm 1886. Công ty được liệt kê vào danh sách 500 công ty hàng đầu của Fortunes. J&J luôn được biết đến là một công ty danh tiếng, tạp chí Barron xếp J&J như là công ty đáng chú ý nhất và nó là công ty đầu tiên được nhận giải Benjamin Franklin cho công ty dược phẩm ở Hoa Kỳ vì các nguồn tài trợ cho chương trình đào tạo giáo dục Quốc tế.', '2019-09-05', '2019-09-05');
